const t=JSON.parse('{"key":"v-7b031672","path":"/content/%E4%B8%83%E4%B9%89%E5%8F%A5.html","title":"七义句","lang":"zh-CN","frontmatter":{"title":"七义句","description":"【《金刚般若论》学习笔记】思维导图：七义句。（主要依照《金刚般若论》(宋元版)）","pdf":"/pdf/七义句.pdf","scale":1.2,"associatedInfo":[{"x":259,"y":48,"kind":"备注","text":"此般若波罗蜜，为佛种不断故流行于世，为显此当得佛种不断义故。 \\n"},{"x":259,"y":86,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n二发起行相者。发起了于。行谓般若行解。即慧体名相。以无相故。非是相状。如十六行慧胜得名。三问皆因慧行相。佛所正答。是慧行相。今为问起。名发起于行相。能起佛说。慧行相故。或此三问。皆问所修行之体状。名为行相。于中修慧虽标总称。而得自名。别名修行。若依前解。虽诸福智有无为等皆所修诸法。慧为般若之体。独得其名。若依后解。唯在有为。无为非行。便成大过。以问有为行。无为自证亦无有失。以此理微。\\n>\\n"},{"x":259,"y":124,"kind":"备注","text":"「行所住处」者，谓彼发起行相所住处也\\n<\\n参见《金刚般若论会释》：\\n行所住处者。行谓三问所发起行相。住处谓所依止。彼修行相所依止道理处所。\\n>\\n"},{"x":92,"y":155,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n所明名义。能诠名句。以教诠义。名为有义句。义之句故。或义是所诠。文为能诠。以文随义。皆有七故。名七义句。若文若义。皆有七故。\\n\\n参见《金刚般若经赞述》：\\n此七之初三科判此经，总有三段；后四彰此经中所有之义，非别判文也。三段文者： \\n（一）善现赞叹名为种姓不断。 \\n（二）善现发请名为发起行相，发问修行之相故。 \\n（三）者如来印答，讫至「应作如是观」名行所住处，谓依佛所说此一部义以行其行，即是行所住处也。\\n>\\n"}],"head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%E4%B8%83%E4%B9%89%E5%8F%A5.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"七义句"}],["meta",{"property":"og:description","content":"【《金刚般若论》学习笔记】思维导图：七义句。（主要依照《金刚般若论》(宋元版)）"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"七义句　| 《金刚般若论》学习笔记（思维导图）\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/七义句.md"}');export{t as data};
