const t=JSON.parse('{"key":"v-94f3f35c","path":"/content/%E7%BB%8F%E6%96%8705.html","title":"正释经文05","lang":"zh-CN","frontmatter":{"title":"正释经文05","pdf":"/pdf/经文05.pdf","scale":1.2,"associatedInfo":[{"x":619,"y":200,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n初凡所有相皆是虚妄。是摄持欲愿。显有为相。是虚妄。翻明异此虚妄无相之相。非是虚妄。令其摄持后发生欲愿。非是欲愿摄持有相虚妄之法。\\n>\\n"},{"x":566,"y":279,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n次第二云。若见诸相非相。则非虚妄。是安立第一义。相者为虚妄化身。非相为真实法身。见化身诸有为相。非法身之无相。则非虚妄。彼以化身有相。为真实身相。故是虚妄。 \\n>\\n"}],"description":"【「须菩提！于意云何？可以相成就见如来不？」须菩提言：「不也！世尊！不可以相成就得见如来。何以故？如来所说相，即非相。」佛告须菩提：「凡所有相，皆是妄语。若见诸相非相，则非妄语。如是诸相非相，则见如来。」】","head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%E7%BB%8F%E6%96%8705.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"正释经文05"}],["meta",{"property":"og:description","content":"【「须菩提！于意云何？可以相成就见如来不？」须菩提言：「不也！世尊！不可以相成就得见如来。何以故？如来所说相，即非相。」佛告须菩提：「凡所有相，皆是妄语。若见诸相非相，则非妄语。如是诸相非相，则见如来。」】"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"正释经文05　| 《金刚般若论》学习笔记（思维导图）\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/经文05.md","autoDesc":true}');export{t as data};
