const t=JSON.parse('{"key":"v-874d2e64","path":"/content/%E7%BB%8F%E6%96%8709.html","title":"正释经文09","lang":"zh-CN","frontmatter":{"title":"正释经文09","pdf":"/pdf/经文09.pdf","scale":1.2,"associatedInfo":[{"x":380,"y":446,"kind":"备注","text":"<\\n参见（宋元版）：\\n经言：「世尊！无有所入，不入色声香味触」者\\n>\\n"},{"x":769,"y":456,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n后解说二无诤行所以。内证真理。离我所执。住无诤理。无有少法。得阿罗汉。外住无诤。都无所行。行无诤行。由理及行。二种无诤故。经重说无诤无诤行。以无少法及无所行。名为安立第一义也。\\n>\\n"}],"description":"【「须菩提！于意云何？须陀洹能作是念：『我得须陀洹果』不？」。须菩提言：「不也！世尊！何以故？实无有法名须陀洹。不入色声香味触法，是名须陀洹。」佛言：「须菩提！于意云何？斯陀含能作是念：『我得斯陀含果』不？」须菩提言：「不也！世尊！何以故？实无有法名斯陀含。是名斯陀含。」「须菩提！于意云何？阿那含能作是念：『我得阿那含果』不？」须菩提言：「不也！世尊！...","head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%E7%BB%8F%E6%96%8709.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"正释经文09"}],["meta",{"property":"og:description","content":"【「须菩提！于意云何？须陀洹能作是念：『我得须陀洹果』不？」。须菩提言：「不也！世尊！何以故？实无有法名须陀洹。不入色声香味触法，是名须陀洹。」佛言：「须菩提！于意云何？斯陀含能作是念：『我得斯陀含果』不？」须菩提言：「不也！世尊！何以故？实无有法名斯陀含。是名斯陀含。」「须菩提！于意云何？阿那含能作是念：『我得阿那含果』不？」须菩提言：「不也！世尊！..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"正释经文09　| 《金刚般若论》学习笔记（思维导图）\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/经文09.md","autoDesc":true}');export{t as data};
