const t=JSON.parse('{"key":"v-5283df76","path":"/content/%E7%BB%8F%E6%96%8734.html","title":"正释经文34","lang":"zh-CN","frontmatter":{"title":"正释经文34","pdf":"/pdf/经文34.pdf","scale":1.2,"associatedInfo":[{"x":767,"y":92,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n观能说所说体空。虽恒演说。如不演说。\\n>\\n"},{"x":722,"y":117,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n以名利心说。则虽恒说。如不演说。\\n>\\n"},{"x":244,"y":305,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n前总说佛不住生死。由达法无我。此中别说九种观行。显于流转不染。即是成前不住生死义。又释前虽说佛不见有生死可住。何妨生死是有故。此观佛九种。生死法皆虚妄。如星翳等故。佛虽处生死。生死不能染污。是名流转不染。\\n>\\n"},{"x":938,"y":324,"kind":"备注","text":"若有受皆是苦，以三苦故，随有应知，彼苦生故，是苦苦；破灭故，是坏苦；不相离故，是行苦。复于第四禅及无色中，立不苦不乐受，以胜故。\\n"},{"x":833,"y":349,"kind":"批注","text":"依个人理解，此处是指凡夫以攀缘故，执人法为有。\\n"}],"description":"【「须菩提！若有菩萨摩诃萨以满无量阿僧祇世界七宝，持用布施。若有善男子善女人，发菩萨心者，于此般若波罗蜜经，乃至四句偈等，受持读诵，为他人说，其福德胜彼无量阿僧祇。云何为人演说而不名说，是名为说。」尔时，世尊而说偈言：「一切有为法，如星翳灯幻，露泡梦电云，应作如是观」】","head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%E7%BB%8F%E6%96%8734.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"正释经文34"}],["meta",{"property":"og:description","content":"【「须菩提！若有菩萨摩诃萨以满无量阿僧祇世界七宝，持用布施。若有善男子善女人，发菩萨心者，于此般若波罗蜜经，乃至四句偈等，受持读诵，为他人说，其福德胜彼无量阿僧祇。云何为人演说而不名说，是名为说。」尔时，世尊而说偈言：「一切有为法，如星翳灯幻，露泡梦电云，应作如是观」】"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"正释经文34　| 《金刚般若论》学习笔记（思维导图）\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/经文34.md","autoDesc":true}');export{t as data};
