const t=JSON.parse('{"key":"v-23a26c1a","path":"/content/%EF%BC%88%E4%B8%80%EF%BC%89%E7%A7%8D%E6%80%A7%E4%B8%8D%E6%96%AD.html","title":"七义句：（一）种性不断","lang":"zh-CN","frontmatter":{"title":"七义句：（一）种性不断","description":"【《金刚般若论》学习笔记】思维导图：七义句（一）种性不断。（主要依照《金刚般若论》(宋元版)）","pdf":"/pdf/（一）种性不断.pdf","scale":1.2,"associatedInfo":[{"x":896,"y":24,"kind":"备注","text":"<\\n关于“五种义”，参见《金刚般若论会释》：\\n逐难释云。于中善摄等者。合经及二论。摄付有六义。\\n......\\n五所摄付法。论云。五种义中。菩萨法而建立故。或有解云。谓五乘法中菩萨之法。若尔云何名为五义。不名五乘。既法付五乘。如何言摄付菩萨。今为二解。一云。即下重释前之玄宗之巨委中。释布施云。自此已后。余住处中。有五种随所相应而解释。一依义。二说相。三摄持。四安立。五显现。即是经中所明。理事二行。善恶二门。菩萨修断之法。是佛二时所摄付法。二云。即前七义句中。后五句义所明菩萨行法。前二门问。后五佛答。是正所明能摄付法。\\n......\\n>\\n"},{"x":123,"y":50,"kind":"备注","text":"此般若波罗蜜，为佛种不断故流行于世，为显此当得佛种不断义故。 \\n"}],"head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%EF%BC%88%E4%B8%80%EF%BC%89%E7%A7%8D%E6%80%A7%E4%B8%8D%E6%96%AD.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"七义句：（一）种性不断"}],["meta",{"property":"og:description","content":"【《金刚般若论》学习笔记】思维导图：七义句（一）种性不断。（主要依照《金刚般若论》(宋元版)）"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"七义句：（一）种性不断　| 《金刚般若论》学习笔记（思维导图）\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/（一）种性不断.md"}');export{t as data};
