const t=JSON.parse('{"key":"v-5752deb1","path":"/content/%EF%BC%88%E4%BA%8C%EF%BC%89%E5%8F%91%E8%B5%B7%E8%A1%8C%E7%9B%B8.html","title":"七义句：（二）发起行相","lang":"zh-CN","frontmatter":{"title":"七义句：（二）发起行相","description":"【《金刚般若论》学习笔记】思维导图：七义句（二）发起行相。","pdf":"/pdf/（二）发起行相.pdf","scale":1.2,"associatedInfo":[{"x":123,"y":114,"kind":"备注","text":"<\\n参见《金刚般若论会释》：\\n二发起行相者。发起了于。行谓般若行解。即慧体名相。以无相故。非是相状。如十六行慧胜得名。三问皆因慧行相。佛所正答。是慧行相。今为问起。名发起于行相。能起佛说。慧行相故。或此三问。皆问所修行之体状。名为行相。于中修慧虽标总称。而得自名。别名修行。若依前解。虽诸福智有无为等皆所修诸法。慧为般若之体。独得其名。若依后解。唯在有为。无为非行。便成大过。以问有为行。无为自证亦无有失。以此理微。\\n>\\n"}],"head":[["meta",{"property":"og:url","content":"http://www.jgbrl-mindmap.com/content/%EF%BC%88%E4%BA%8C%EF%BC%89%E5%8F%91%E8%B5%B7%E8%A1%8C%E7%9B%B8.html"}],["meta",{"property":"og:site_name","content":"《金刚般若论》学习笔记（思维导图）"}],["meta",{"property":"og:title","content":"七义句：（二）发起行相"}],["meta",{"property":"og:description","content":"【《金刚般若论》学习笔记】思维导图：七义句（二）发起行相。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"七义句：（二）发起行相\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[],"git":{},"filePathRelative":"content/（二）发起行相.md"}');export{t as data};
